import 'package:flutter/material.dart';
import 'package:lista_series/widgets/incrementa_decrementa.dart';
import 'package:flutter/services.dart';
import 'dart:convert';
import 'package:http/http.dart' as http;

const _kImageBase64 =
    "";

class SerieFormPage extends StatefulWidget {
  const SerieFormPage({Key? key}) : super(key: key);

  @override
  State<SerieFormPage> createState() => _SerieFormPageState();
}

class _SerieFormPageState extends State<SerieFormPage> {
  final TextEditingController _nombreController = TextEditingController();
  int _temporada = 1;
  int _capitulo = 1;
  Uint8List? _imagen;
  bool _vista = false;
  bool _aparcada = false;
  TextEditingController mytext = TextEditingController();

  void _getDataHtml(url) async {
    try {
      final response = await http.get(Uri.parse(url));
      _imagen = response.bodyBytes;
    } on Exception catch (_) {
      _imagen = null;
    }
    setState(() {});
  }

  void _getClipboard(mytext) async {
    ClipboardData? data = await Clipboard.getData(Clipboard.kTextPlain);
    var url = (data!.text == null) ? '' : data.text;
    mytext.text = url;
    _getDataHtml(url);
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
        appBar: AppBar(
          title: const Text('AÃ±adir nueva serie'),
          backgroundColor: Colors.teal,
          centerTitle: true,
        ),
        body: Padding(
            padding: const EdgeInsets.all(12.0),
            child: Column(
                crossAxisAlignment: CrossAxisAlignment.stretch,
                children: [
                  TextField(
                    controller: _nombreController,
                    decoration: const InputDecoration(
                      border: OutlineInputBorder(),
                      hintText: 'Introduzca el nombre de la serie',
                    ),
                  ),
                  const SizedBox(height: 16.0),
                  IncrementaDecrementa(
                    titulo: 'Temporada',
                    valor: _temporada.toDouble(),
                    onIncrementa: () {
                      setState(() {
                        _temporada++;
                      });
                    },
                    onDecrementa: () {
                      setState(() {
                        (_temporada > 1) ? _temporada-- : _temporada;
                      });
                    },
                  ),
                  const SizedBox(height: 16.0),
                  IncrementaDecrementa(
                    titulo: 'CapÃ­tulo',
                    valor: _capitulo.toDouble(),
                    onIncrementa: () {
                      setState(() {
                        _capitulo++;
                      });
                    },
                    onDecrementa: () {
                      setState(() {
                        (_capitulo > 1) ? _capitulo-- : _capitulo;
                      });
                    },
                  ),
                  const SizedBox(height: 16.0),
                  const Text(
                    'Esta vista',
                    style: TextStyle(
                      fontSize: 16.0,
                      fontWeight: FontWeight.w500,
                    ),
                  ),
                  Checkbox(
                    value: _vista,
                    onChanged: (bool? value) {
                      setState(() {
                        _vista = value!;
                      });
                    },
                  ),
                  const Text(
                    'Esta aparcada',
                    style: TextStyle(
                      fontSize: 16.0,
                      fontWeight: FontWeight.w500,
                    ),
                  ),
                  Checkbox(
                    value: _aparcada,
                    onChanged: (bool? value) {
                      setState(() {
                        _aparcada = value!;
                      });
                    },
                  ),
                  const SizedBox(height: 16.0),
                  const Text(
                    'Imagen',
                    style: TextStyle(
                      fontSize: 16.0,
                      fontWeight: FontWeight.w500,
                    ),
                  ),
                  Visibility(
                      visible: false,
                      child: TextField(
                        controller: mytext,
                      )),
                  MaterialButton(
                    onPressed: () async {
                      _getClipboard(mytext);
                    },
                    child: SizedBox(
                        width: 200.0,
                        height: 200.0,
                        child: AspectRatio(
                            aspectRatio: 16 / 9,
                            child: (_imagen != null)
                                ? Image.memory(_imagen!)
                                : Image.memory(base64Decode(_kImageBase64)))),
                  ),
                ])));
  }
}
